import "./news.css";
import Marquee from "react-fast-marquee";
import newsContent from "./news.json";
import { useState } from "react";

function News() {

    const [activeNew, setActiveNew] = useState("1");

    return (
        <div className='news'>
            <div className='news-container'>
                <div className='new-container-left'>
                    <div className='big-new-image'></div>
                    <div className='big-new'></div>
                    <div className='big-new-title glitch2' data-content='GIVE YOUR SECRETLAB TITAN EVO A NEW LOOK WITH THE SECRETLAB SKINS CYBERPUNK: EDGERUNNERS COLLECTION'>
                        GIVE YOUR SECRETLAB TITAN EVO A NEW LOOK WITH THE SECRETLAB SKINS CYBERPUNK: EDGERUNNERS COLLECTION
                    </div>
                </div>
                <div className='new-container-right'>

                    <Marquee
                        autoFill={true}
                        direction="down"
                        className="marquee1"
                        speed={35}
                    >
                        {
                            newsContent.map((item, ind) => (
                                <div
                                    className={`itm itm-${item.id} ${item.id === activeNew ? 'itm-active' : null}`}
                                    key={ind}
                                    onClick={setActiveNew(item.id)}
                                ></div>
                            ))
                        }
                    </Marquee>

                    <Marquee
                        autoFill={true}
                        direction="up"
                        className="marquee1"
                        speed={30}
                    >
                        {
                            newsContent.map((item, ind) => (
                                <div
                                    className={`itm itm-${item.id} ${item.id === activeNew ? 'itm-active' : null}`}
                                    key={ind}
                                    onClick={setActiveNew(item.id)}
                                ></div>
                            ))
                        }
                    </Marquee>

                </div>
            </div>
        </div>
    )
}
export default News;
