import './App.css';
import Head from '../components/head/head';
import News from '../components/news/news';
import Products from '../components/products/products';
import Footer from '../components/footer/footer';

//social renklendirme
//tablet telefon uyumluluk
function App() {

  var hedefElement = document.getElementById('news');

  function scrollOlayi() {
    console.log("Hedef element görüldü!");
  }

  window.addEventListener("scroll", function () {
    var hedefElementYukseklik = hedefElement.offsetTop;
    var pencereYukseklik = window.innerHeight;
    var yukseklikFarki = hedefElementYukseklik - window.scrollY;

    if (yukseklikFarki < pencereYukseklik) {
      scrollOlayi();
    }
  });

  return (
    <div className="App">
      <Head />
      <News />
      <Products />
      <Footer />
    </div>
  );
}
export default App;
